        name: {
            type: String
        },

        description: {
            type: String
        },

        price: {
            type: Number
        },

        soldout: {
            type: String
        },

        inventory: {
            type: String

        },
        stores: {
            type: Array
        }
});

const Widgets = mongoose.model('widgets', widgetSchema);

const sequelize = new Sequelize('sqlite::memory:');

const Categories = sequelize.define('categories', {
    category: DataTypes.STRING,
    description: DataTypes.STRING,
});

